set serveroutput on;--Recapitulando lo que es un bloque pl sqlDECLARE nombre varchar2(20):='Victor Blake';BEGINDBMS_OUTPUT.PUT_LINE('Hola Guapo ' || nombre);end;/DECLARE EDAD INTEGER:=21;DIAS INTEGER;ESTATUS VARCHAR2(12);BEGINDIAS:=EDAD*365;IF DIAS >10000 THENESTATUS:='VETERANO';ELSEESTATUS:='NOVATO';END IF;DECLARE EDAD INTEGER:=21;DIAS INTEGER;ESTATUS VARCHAR2(12);BEGINDIAS:=EDAD*365;IF DIAS >10000 THENESTATUS:='VETERANO';ELSEESTATUS:='NOVATO';END IF;DBMS_OUTPUT.PUT_LINE('TU EDAD EN DIAS ES ' || DIAS||'ESTATUS'||ESTATUS);END;/END;/--VEREMOS NUESTRO PRIMER PROCEDIMIENTO ALMACENADOCREATE OR REPLACE PROCEDURE saludar (mensaje IN VARCHAR2)AS BEGINDBMS_OUTPUT.PUT_LINE('HOLA BUENAS NOCHES'|| MENSAJE);END;/--Â  Ejecutamos el procedimientoexec saludar ('Vichu Blake');-- generamos la secuenciacreate sequence sec_personastart with 1increment by 1nomaxvalue;------Generamos la tablacreate table PERSONA (ID_PERSONA INTEGER, NOMBRE VARCHAR2 (20), EDAD INTEGER, CONSTRAINT PK_ID_PERSONA PRIMARY KEY (ID_PEROSNA));
